import React, { Component, PropTypes } from 'react';
import { web3 } from '../lib/web3';
import ContractFunc from './ContractFunc';


export default class CrownsaleContract extends Component {
    constructor(props) {
        super(props);

        this.isRendered = false;

        let browser_untitled1_sol_crowdsaleContract = web3.eth.contract([{"constant":false,"inputs":[],"name":"finishMinting","outputs":[],"payable":false,"stateMutability":"nonpayable","type":"function"},{"constant":true,"inputs":[],"name":"owner","outputs":[{"name":"","type":"address"}],"payable":false,"stateMutability":"view","type":"function"},{"constant":false,"inputs":[],"name":"createTokens","outputs":[],"payable":true,"stateMutability":"payable","type":"function"},{"constant":false,"inputs":[{"name":"newOwner","type":"address"}],"name":"transferOwnership","outputs":[],"payable":false,"stateMutability":"nonpayable","type":"function"},{"constant":true,"inputs":[],"name":"token","outputs":[{"name":"","type":"address"}],"payable":false,"stateMutability":"view","type":"function"},{"inputs":[{"name":"multisig","type":"address"},{"name":"restricted","type":"address"},{"name":"restrictedPercent","type":"uint256"},{"name":"rate","type":"uint256"},{"name":"start","type":"uint256"},{"name":"period","type":"uint256"},{"name":"hardcap","type":"uint256"}],"payable":false,"stateMutability":"nonpayable","type":"constructor"},{"payable":true,"stateMutability":"payable","type":"fallback"}]);
        this.contract = browser_untitled1_sol_crowdsaleContract.at(this.props.contractAddress);
    }

    render() {
        if(!this.contract){
            return;
        }
        return (
            <section className="container" ref="panel">
                <div className="login">
                    <h1>Crownsale ({this.props.contractAddress})</h1>
                    <p>{this.renderAbi()}</p>
                </div>
            </section>
        );
    }

    renderAbi(){
        if(this.contract.abi.length==0){
            return;
        }
        let funcAbi = this.contract.abi.filter((elem)=>{return elem.type=='function'}).sort((x,y)=>{
            return !(x.constant||(!x.constant&&!y.constant));
        });
        let result = new Array();
        for(let i = 0;i<funcAbi.length;i++){
            result.push((
                <ContractFunc contractRealization={this.contract} abielem={funcAbi[i]}/>
            ));
        }
        return result;
    }

    handleEnter(){
        let result = web3.personal.unlockAccount(this.refs.accountSelector.value, this.refs.password.value);
        console.log(this.refs.password.value + result);
    }

    componentDidMount() {
        this.isRendered = true;
        if(!this.props.hide){
            this.show();
        }else{
            this.hide();
        }
    }

    show(){
        this.refs.panel.style.display = 'block';
        this.props.hide = false;
    }

    hide(){
        this.refs.panel.style.display = 'none';
        this.props.hide = true;
    }
}

CrownsaleContract.propTypes = {
    contractAddress:PropTypes.string.isRequired,
    hide:PropTypes.bool.isRequired,
};
