import React, { Component, PropTypes } from 'react';
import ReactDOM from 'react-dom';
import { createContainer } from 'meteor/react-meteor-data';
import { web3 } from '../lib/web3';
import RegistrationPanel from "./RegistrationPanel"

// my RPC settings are:
// geth --rpc --rpcaddr="0.0.0.0" --rpccorsdomain="*" --mine --unlock=YOUR_ACCOUNT --verbosity=5 --maxpeers=0 --minerthreads="3"
class App extends Component {
    constructor(props){
        super(props);

        this.isRendered = false;
        this.currentTab = 'NetworkParams';

        web3.eth.defaultAccount = web3.eth.accounts[0];
    }

    componentDidMount() {
        this.isRendered = true;
        this.refs[this.currentTab + 'Link'].className += " active";
    }

    render() {
        return (
            <div className="container">
                <header>
                    <h1>Crownsale Test</h1>
                </header>
                <ul>
                    <RegistrationPanel ref="BalanceInfo" className="tabcontent"/>
                </ul>
            </div>
        );
    }
}

App.propTypes = {
};

export default createContainer(() => {
    return {
    };
}, App);
