import React, { Component, PropTypes } from 'react';
import { web3 } from '../lib/web3';

export default class RegistrationPanel extends Component {
    constructor(props) {
        super(props);

        this.isRendered = false;
    }

    componentDidMount() {
        this.isRendered = true;
    }

    render() {
        return (
            <section className="container">
                <div className="login">
                    <h1>Choose account for work and enter password</h1>
                    <p>{this.renderAccountSelector()}</p>
                    <p><input type="password" ref="password" placeholder="Password"/></p>
                    <p className="submit">
                        <button type="button" onClick={this.handleEnter.bind(this)}>Enter</button>
                    </p>
                </div>
            </section>
        );
    }

    renderAccountSelector() {
        let transSelector = web3.eth.accounts.map((trans) => (
            <option value={trans}>{trans}</option>
        ));
        return (
            <select type="accountOption" ref="accountSelector">
                {transSelector}
            </select>
        );
    }

    handleEnter(){
        let result = web3.personal.unlockAccount(this.refs.accountSelector.value, this.refs.password.value);
        console.log(this.refs.password.value + result);
    }
}

RegistrationPanel.propTypes = {
};
